import { Component, OnInit } from '@angular/core';
import { environment } from 'src/environments/environment';
import { HttpClient } from '@angular/common/http';
import { getLocaleDateFormat } from '@angular/common';
import { Personaje } from 'src/app/interfaces/personaje';


@Component({
  selector: 'app-main-page',
  templateUrl: './main-page.component.html',
  styleUrls: ['./main-page.component.css']
})
export class MainPageComponent implements OnInit {
   personajes: Personaje[] | undefined;
   personajesCopy: Personaje[] | undefined;


  constructor( public http: HttpClient) {

    this.getData();
   }

   async getData(){
    await this.http.get<any>(environment.apiUrl + '/characters')
    .subscribe((res) => {
      this.personajes = res.map(({char_id, name, img, status, occupation, nickname }: Personaje) =>{
        return{
          char_id: char_id,
          name: name,
          img: img,
          status: status,
          occupation: occupation,
          nickname: nickname
        };
      });

      this.personajesCopy = this.personajes;
    });
   }

  ngOnInit(): void {
  }

  filter(e:any){
    const search: string = e.target.value;
   this.personajes = this.personajesCopy?.filter(({ name }:Personaje) => {
    return name.toLowerCase().includes(search.toLowerCase());

   });
  }

}
<div class="container my-5">
    <h1>Breaking Bad Wiki  </h1> <h3 style="text-align:right">RuXxDesigN</h3>
<hr/>
<input type="text" class="form-control" placeholder="Search ... " (input)="filter($event)"/>

<div class="row">
    <div *ngFor="let personaje of personajes" class=".col-xl-4 col-sm-6 col-xs-12">
          <app-card [personaje]="personaje"></app-card>

    </div>
</div>


</div>
